import { QR } from '../QR';

describe('Integration tests', () => {
    it('encodes the input string', () => {
        const input = 'Hello #1';
        const expectedOutput =
            '';
        const mode = 'Byte';
        const errorCorrection = 'L';
        const canvasElement = document.createElement('canvas');
        canvasElement.id = 'canvasElement';

        const qr = new QR(null, input, mode, errorCorrection);

        expect(qr.getCode().join('')).toBe(expectedOutput);
    });
});
